package com.whilerain.animationpractices.lesson1import android.animation.AnimatorInflaterimport android.animation.ObjectAnimatorimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.view.Viewimport com.whilerain.animationpractices.Rimport kotlinx.android.synthetic.main.activity_lesson1.*class Lesson1Activity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson1)        text.setOnClickListener { v ->            //launch a property animation            val set =  AnimatorInflater.loadAnimator(this, R.animator.property_anim)            set.setTarget(v)            set.start()            // launch a tween animation//            val anim = AnimationUtils.loadAnimation(this@Lesson1Activity, R.anim.tween_anim)//            v.startAnimation(anim)        }    }    /**     * Property animation can be launch programmatically     *     * @param view     */    fun rotateyAnimRun(view: View) {        ObjectAnimator                .ofFloat(view, "rotationX", 0.0f, 360.0f)                .setDuration(500)                .start()    }}