package com.whilerain.animationpractices.lesson1import android.animation.AnimatorInflaterimport android.animation.AnimatorSetimport android.animation.ObjectAnimatorimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.view.Viewimport android.view.animation.Animationimport android.view.animation.AnimationUtilsimport com.whilerain.animationpractices.Rclass Lesson1Activity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson1)        val view = findViewById<View>(R.id.text)        view.setOnClickListener { v ->            //launch a property animation            val set =  AnimatorInflater.loadAnimator(this, R.animator.property_scale_size)            set.setTarget(v)            set.start()            // launch a view animation//            val anim = AnimationUtils.loadAnimation(this@Lesson1Activity, R.anim.tween_slide_in_from_buttom)//            v.startAnimation(anim)        }    }    /**     * Property animation can be launch programmatically     *     * @param view     */    fun rotateyAnimRun(view: View) {        ObjectAnimator                .ofFloat(view, "rotationX", 0.0f, 360.0f)                .setDuration(500)                .start()    }}