package com.whilerain.animationpractices.lesson1import android.graphics.drawable.Animatableimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport com.whilerain.animationpractices.Rimport kotlinx.android.synthetic.main.activity_lesson1_frame_anim.*class Lesson1FrameAnimActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson1_frame_anim)        img.setBackgroundResource(R.drawable.frame_anim)        img.setOnClickListener { v ->            val res = v.background            if (res is Animatable) {                if(res.isRunning){                    res.stop()                }                res.start()            }        }    }}