package com.whilerain.animationpractices.lesson2import android.support.v4.view.ViewPagerimport android.util.Logimport android.view.Viewimport android.widget.ImageViewimport android.widget.TextViewimport com.whilerain.animationpractices.Rclass MyPageTransormer : ViewPager.PageTransformer {    override fun transformPage(page: View, position: Float) {        val pageWidth = page.width        val title = page.findViewById<TextView>(R.id.title)        val desc = page.findViewById<TextView>(R.id.desc)        title.translationX = position * 1.2f * pageWidth.toFloat()        desc.translationX = position * 1.7f * pageWidth.toFloat()        if (page.tag == "Intro1Fragment") {            val image1 = page.findViewById<ImageView>(R.id.image1)            val image2 = page.findViewById<ImageView>(R.id.image2)            image1.translationX = -position * 0.7f * pageWidth.toFloat()            image2.translationX = -position * 0.3f * pageWidth.toFloat()        } else if (page.tag == "Intro2Fragment") {            val image1 = page.findViewById<ImageView>(R.id.image1)            val image2 = page.findViewById<ImageView>(R.id.image2)            val image3 = page.findViewById<ImageView>(R.id.image3)            image1.translationX = -position * 0.8f * pageWidth.toFloat()            image2.translationX = -position * 0.8f * pageWidth.toFloat()            image2.rotation = Math.abs(-position * 180)            image3.translationX = -position * 0.8f * pageWidth.toFloat()            image3.rotation = -position * 540        } else if (page.tag == "Intro3Fragment") {            Log.d("TEST", "position of intro3: $position")            val image1 = page.findViewById<ImageView>(R.id.image1)            val image2 = page.findViewById<ImageView>(R.id.image2)            image1.translationX = -position * 0.8f * pageWidth.toFloat()            image2.translationX = -position * 0.5f * pageWidth.toFloat()            image2.rotation = -position * 70        } else if (page.tag == "Intro4Fragment") {            val image1 = page.findViewById<ImageView>(R.id.image1)            val image2 = page.findViewById<ImageView>(R.id.image2)            image1.translationY = -position * 0.5f * page.height.toFloat()            image2.translationY = position * 0.3f * page.height.toFloat()        } else if (page.tag == "GrantGpsFragment") {            val image1 = page.findViewById<ImageView>(R.id.image1)            val image2 = page.findViewById<ImageView>(R.id.image2)            image1.translationX = -position * 0.8f * pageWidth.toFloat()            image2.translationX = -position * 0.8f * pageWidth.toFloat()            image2.rotation = Math.abs(-position * 360)        }    }}