package com.whilerain.animationpractices.lesson4import android.graphics.drawable.AnimatedVectorDrawableimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport com.whilerain.animationpractices.Rimport kotlinx.android.synthetic.main.activity_lesson4.*class Lesson4Activity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson4)        image.setImageDrawable(getDrawable(R.drawable.bluetooth_anim))        image.setOnClickListener {            if (image.drawable is AnimatedVectorDrawable) {                (image.drawable as AnimatedVectorDrawable).start()            }        }    }}