package com.whilerain.animationpractices.lesson4import android.graphics.drawable.AnimatedVectorDrawableimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.view.Viewimport android.widget.ImageViewimport com.whilerain.animationpractices.Rclass Lesson4Activity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson4)        val imageView = findViewById<ImageView>(R.id.image)        imageView.setImageDrawable(getDrawable(R.drawable.avd_anim))        imageView.setOnClickListener {            if (imageView.drawable is AnimatedVectorDrawable) {                (imageView.drawable as AnimatedVectorDrawable).start()            }        }    }}