package com.whilerain.animationpractices.lesson5import android.os.Bundleimport android.support.constraint.ConstraintLayoutimport android.support.constraint.ConstraintSetimport android.support.v7.app.AppCompatActivityimport android.transition.TransitionManagerimport android.view.Viewimport com.whilerain.animationpractices.Rimport kotlinx.android.synthetic.main.activity_lesson5.*class Lesson5Activity : AppCompatActivity() {    private val constraintSet1 = ConstraintSet()    private val constraintSet2 = ConstraintSet()    private var showList: Boolean = false    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson5)        // get constraints from ConstraintSet        constraintSet1.clone(vConstraintLayout)        // get constraints from layout        constraintSet2.clone(this, R.layout.activity_lesson5_1)        vKoso.setOnClickListener {            if (!showList) {                TransitionManager.beginDelayedTransition(vConstraintLayout)                constraintSet2.applyTo(vConstraintLayout)                showList = true            }        }    }    override fun onBackPressed() {        if (showList!!) {            TransitionManager.beginDelayedTransition(vConstraintLayout)            constraintSet1.applyTo(vConstraintLayout)            showList = false        } else {            super.onBackPressed()        }    }}