package com.whilerain.animationpractices.lesson5import android.os.Bundleimport android.support.constraint.ConstraintLayoutimport android.support.constraint.ConstraintSetimport android.support.v7.app.AppCompatActivityimport android.transition.TransitionManagerimport android.view.Viewimport com.whilerain.animationpractices.Rclass Lesson5Activity : AppCompatActivity() {    private var vConstraintLayout: ConstraintLayout? = null    private val constraintSet1 = ConstraintSet()    private val constraintSet2 = ConstraintSet()    private var showList: Boolean = false    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson5)        vConstraintLayout = findViewById(R.id.vConstraintLayout)        constraintSet1.clone(vConstraintLayout!!)        constraintSet2.clone(this, R.layout.activity_lesson5_1)        findViewById<View>(R.id.vKoso).setOnClickListener {            if (!showList) {                TransitionManager.beginDelayedTransition(vConstraintLayout)                constraintSet2.applyTo(vConstraintLayout!!)                showList = true            }        }    }    override fun onBackPressed() {        if (showList!!) {            TransitionManager.beginDelayedTransition(vConstraintLayout)            constraintSet1.applyTo(vConstraintLayout!!)            showList = false        } else {            super.onBackPressed()        }    }}