package com.whilerain.animationpractices.lesson6import android.app.ActivityOptionsimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.util.Pairimport android.view.Viewimport android.widget.Buttonimport android.widget.LinearLayoutimport android.widget.TextViewimport com.whilerain.animationpractices.Rclass Lesson6ShareElementsActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson6_share_elements)        val go = findViewById<Button>(R.id.go)        val title = findViewById<TextView>(R.id.title)        val root = findViewById<LinearLayout>(R.id.root)        go.setOnClickListener {            val intent = Intent(this@Lesson6ShareElementsActivity, Lesson6ShareElement1Activity::class.java)            val options = ActivityOptions                    .makeSceneTransitionAnimation(this@Lesson6ShareElementsActivity, Pair(root, "root"), Pair(title, "title"))            startActivity(intent, options.toBundle())        }    }}