package com.whilerain.animationpractices.lesson6import android.app.ActivityOptionsimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport com.whilerain.animationpractices.Rimport kotlinx.android.synthetic.main.activity_lesson6_share_elements.*import android.util.Pair as UtilPairclass Lesson6ShareElementsActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lesson6_share_elements)        go.setOnClickListener {            val intent = Intent(this, Lesson6ShareElement1Activity::class.java)            val options = ActivityOptions.makeSceneTransitionAnimation(this, UtilPair.create(vRoot, "root"))            startActivity(intent, options.toBundle())        }        go1.setOnClickListener{            val intent = Intent(this, Lesson6ShareElement1Activity::class.java)            val options = ActivityOptions.makeSceneTransitionAnimation(this, UtilPair.create(vRoot1, "root"))            startActivity(intent, options.toBundle())        }    }}